mapper.select:
SELECT COUNT(*)
FROM user
WHERE email = #{email} AND password = #{password};

SELECT COUNT(*)
FROM user
WHERE email = #{email};

SELECT count(*)
FROM user_follow
WHERE user_id = #{user_id}

SELECT count(*)
FROM user_follow
WHERE follow_id = #{user_id}

mapper.insert:
INSERT INTO user_follow (
    user_follow.user_id,
    user_follow.follow_id
)
VALUES (#{user_id}, #{follow_id});

mapper.select:
SELECT id, name, avatar
FROM user
WHERE email = #{email};

SELECT id, name, avatar, short_intro
FROM user
WHERE id = #{id};
